import { useContext, useState } from 'react'; 
import Modal from '../UI/Modal';
import classes from './Cart.module.css';
import CartContext from '../../store/cart-context';
import CartItem from './CartItem';
import Checkout from './Checkout';

const Cart = props => {
    const [isCheckout, setCheckout] = useState(false); 
    const cartCtx = useContext(CartContext);
    const totalAmout = `$${cartCtx.totalAmount.toFixed(2)}`;

    const hasItems = cartCtx.items.length > 0;

    const cartItemRemoveHandler = id => {
        cartCtx.removeItem(id);
    };
    const cartItemAddHandler = item => {
        cartCtx.addItems({...item, amount:1});
    };

    const orderHandler = () => {
        setCheckout(true);
    }

    const cartItems = <ul className={classes['cart-items']}>{(cartCtx.items.map(item => <CartItem
         key={item.id}
         name={item.name} 
         amount={item.amount} 
         price={item.price} 
         onRemove={cartItemRemoveHandler.bind(null, item.id)}
         onAdd={cartItemAddHandler.bind(null, item)}
         />))}</ul>

    const modalActions = <div className={classes.actions}>
    <button className={classes['button--alt']} onClick={props.onCartClose} >Close</button>
    {hasItems && <button className={classes.button} onClick={orderHandler}>Order</button>}  
    </div>

    return <Modal onCartClose={props.onCartClose}>
        {cartItems}
        <div className={classes.total}>
            <span>Total Amount</span>
            <span>{totalAmout}</span>
        </div>
        {isCheckout && <Checkout/>}
        {!isCheckout && modalActions}
    </Modal>
}

export default Cart;
