import Modal from '../UI/Modal';
import classes from './Cart.module.css';



const Cart = props => {
    const itemObj ={id:'c1', name:'Sushi', amount:2, price:12.99}
    const cartItems = <ul className={classes['cart-items']}>{(itemObj &&  itemObj.map(item => <li>{item.name}</li>))}</ul>
    return <Modal>
        {cartItems}
        <div className={classes.total}>
            <span>Total Amount</span>
            <span>35.63</span>
        </div>
        <div className={classes.actions}>
            <button className={classes['button--alt']} onClick={props.onCartClose}>Close</button>
            <button className={classes.button}>Order</button>
        </div>
    </Modal>
}

export default Cart;
